import React, {Component} from 'react';
import './Post.css';

export class CommentSection extends Component {
    constructor(props)
    {
        super(props);
        this.state = {
            hasComments: false,
            postId: this.props.postId,
            commentIds: []
        }
    }

    componentDidMount() {
        // Load the comments for this post
        this.retrieveFeed()
            .then(res =>
            this.setState({
                hasComments: true,
                commentIds: res.commentIds
            }));
    }

    retrieveFeed = async () => {
        let url = '/retrieve_comments?postId=' + this.props.postId;
        const response = await fetch(url)

        const json = await response.json();

        if (response.status !== 200) {
            throw Error(json.message)
        }
        return json;
    }

    renderComments = () =>
    {
        let comments = [];

        // Add the posts
        let commentIds = this.state.commentIds;
        for (let i = 0; i < commentIds.length; ++i)
        {
            comments.push(<CommentBox comment = {commentIds[i]}/>);
        }

        return comments;
    }

    render()
    {
        return(
            <div className="CommentSection-container">
                CommentSection
                {this.renderComments()}
            </div>
        );
    }
}

export class AddComment extends Component {
    constructor(props)
    {
        super(props);
        this.state = {
            postId: this.props.postId,
        }
    }

    render() {
        return (
            <div className="AddComment-container">

            </div>
        );
    }
}

export class CommentBox extends Component {
    render() {
        return (
            <div className='Comment-container'>
                <p>Comment {this.props.comment.timestamp - new Date().getDate()}</p>
                <p></p>
                {this.props.comment.message}
                {this.props.comment.userId}
            </div>
        );
    }
}
