import React, {Component} from 'react';
import './Post.css';

export class CommentSection extends Component {
    constructor(props)
    {
        super(props);
        this.state = {
            postId: this.props.postId,
            commentIds: []
        }
    }

    componentDidMount() {
        // Load the comments for this post
        this.retrieveFeed()
            .then(res => {
            this.setState({
                commentIds: res.commentIds
            });
    });
    }

    retrieveFeed = async () => {
        let url = '/retrieve_comments?postId=' + this.props.postId;
        const response = await fetch(url)

        const json = await response.json();

        if (response.status !== 200) {
            throw Error(json.message)
        }
        return json;
    }

    // Translates this.state.commentIds into legible React Comment objects
    renderComments = () =>
    {
        let posts = [];

        // Add the posts
        let postIds = this.state.postIds;
        for (let i = 0; i < postIds.length; ++i)
        {
            posts.push(<Post postId = {postIds[i].postid}/>);
        }

        return posts;
    }

    render() {
        return (
            <div className="CommentSection-container">

            </div>
        );
    }
}

