import React, {Component} from 'react';
import './Post.css';

export class CommentSection extends Component {
    constructor(props)
    {
        super(props);
        this.state = {
            postId: this.props.postId,
            commentIds: []
        }
    }

    componentDidMount() {
        // Load the comments for this post
        this.retrieveFeed()
            .then(res => {
            this.setState({
                commentIds: res.commentIds
            });
    });
    }

    retrieveFeed = async () => {
        let url = '/retrieve_comments?postId=' + this.props.postId;
        const response = await fetch(url)

        const json = await response.json();

        if (response.status !== 200) {
            throw Error(json.message)
        }
        return json;
    }

    // Translates this.state.commentIds into legible React Comment objects
    renderComments = () =>
    {
        let comments = [];

        // Add the comments
        let commentIds = this.state.commentIds;
        for (let i = 0; i < commentIds.length; ++i)
        {
            comments.push(<Comment commentId = {commentIds[i].commentId}/>);
        }

        return comments;
    }

    render() {
        return (
            <div className='CommentSection-container'>
                {this.renderComments}
            </div>
        );
    }
}

export class AddComment extends Component {
    constructor(props)
    {
        super(props);
        this.state = {
            postId: this.props.postId,
        }
    }

    render() {
        return (
            <div className="AddComment-container">
            // Has the comments above
