            </div>
        );
    }
}

export class Comment extends Component {
    constructor(props)
    {
        super(props);
        this.state = {
            postId: this.props.postId,
        }
    }

    render() {
        return (
            <div className='Comment-container'>
                <p>Comment</p>

            </div>
        );
    }
}
