import React, {Component} from 'react';
import MessageList from './MessageList.js';
import SendMessageForm from './SendMessageForm.js'


const DUMMY_DATA = [
    {
        senderId: 'user1',
        text: 'Hey, how is it going?'
    },
    {
        senderId: 'user2',
        text: 'Great! How about you?'
    },
    {
        senderId: 'user1',
        text: 'Good to hear! I am great as well'
    }
]



class Chat extends React.Component {



    constructor() {
        super()
        this.state = {
            messages: [],
            text: '',
            roomIds: [],
            number: 0
        }

        this.sendMessage = this.sendMessage.bind(this)

    }

    componentDidMount() {
        // Load the feed contents
        this.retrieveMessages()
            .then(res => this.setState( {
               // messages: res.postIds
                messages: res.messages,
                number: res.number
            }));

        this.retrieveRoomIds()
            .then(res => this.setState( {
                // messages: res.postIds
                roomIds: res.roomIds

            }));


    }




    // need to retrieve messages
    retrieveMessages = async () => {
        let url = '/retrieve_messages?userId=' + this.props.myUserId;
        const response = await fetch(url)
        const json = await response.json();

        if (response.status !== 200) {
            throw Error(json.messages)
        }


        return json;
    }

    retrieveRoomIds = async () => {
        let url = '/retrieve_roomIds?userId=' + this.props.myUserId;
        const response = await fetch(url)
        const json = await response.json();

        if (response.status !== 200) {
            throw Error(json.messages)
        }


        return json;
    }



    sendMessage(text) {
        this.setState( {
            // messages: res.postIds
            text: text
        })
        //console.log(this.state)
        this.postMessage(this.state.text)

    }


    postMessage(text)
    {
        let chatmessageid = this.props.myUserId.toString() + this.state.roomIds[0].sessionId.toString() + this.state.number.toString();
        this.addMessage(chatmessageid);
    }

    // Actually sending an API call to post comment to database
    addMessage = function(chatmessageid){
        this.postMsg(chatmessageid)
            .then(
                this.props.refresh(),
                this.setState({message: ""})
            );
    }

    postMsg = async (chatmessageid) =>
    {
        let url = '/post_message';
        const response = await fetch(url,
            {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({chatMessageId: chatmessageid, message: this.state.text, chatSessionId: this.state.roomId[0].sessionId, userId: this.props.myUserId})
            });

        const json = await response.json();

        if (response.status !== 200) {
            throw Error(json.message)
        }

        return json;
    }


    loadChat() {
        // Load the chat for this post
        this.retrieveMessages()
            .then(res => this.setState( {
                // messages: res.postIds
                messages: res.messages,
                number: res.number
            }));
    }

    refreshMessage = () =>
    {
        this.messageList.loadmessage();
    }

    render() {
        //if (this.state.roomIds[0] == undefined) {} else {console.log(this.state.roomIds[0].sessionId)}
        //            text: '',
        //roomIds: [],
         //   number: 0
        return (
        <div className="Chat">
            <MessageList messages={this.state.messages} onRef={ref => (this.messageList = ref)} retrieveMessages={this.retrieveMessages}/>
            <SendMessageForm  sendMessage={this.sendMessage} refresh={this.refreshMessage}/>
            </div>

    )
    }
}

export default Chat
