import React, {Component} from 'react';
import './Post.css';

export class CommentSection extends Component {
    constructor(props)
    {
        super(props);
        this.state = {
            hasComments: false,
            postId: this.props.postId,
            comments: []
        }
    }

    componentDidMount() {
        // Load the comments for this post
        this.retrieveFeed()
            .then(res =>
            this.setState({
                hasComments: true,
                comments: res.commentIds
            }));
    }

    retrieveFeed = async () => {
        let url = '/retrieve_comments?postId=' + this.props.postId;
        const response = await fetch(url)

        const json = await response.json();

        if (response.status !== 200) {
            throw Error(json.message)
        }
        return json;
    }

    renderComments = () =>
    {
        let posts = [];

        // Add the posts
        let postIds = this.state.comments;
        for (let i = 0; i < postIds.length; ++i)
        {
            posts.push(<CommentBox postId = {postIds[i].postid}/>);
        }

        return posts;
    }

    render()
    {
        console.log("render" + this.state.postId)
        return(
            <div className="CommentSection-container">
                CommentSection
                <CommentBox postId={2} commentId={3} />
                {this.renderComments()}
            </div>
        );
    }
}

export class AddComment extends Component {
    constructor(props)
    {
        super(props);
        this.state = {
            postId: this.props.postId,
        }
    }

    render() {
        return (
            <div className="AddComment-container">

            </div>
        );
    }
}

export class CommentBox extends Component {
