import React, {Component} from 'react';
import Options from '../admin/Options';
import UserManagement from '../admin/UserManagement';


class AdminBody extends Component {
    constructor(props) {
        super(props);
  
        this.state = {
            show: this.props.show,
            users: null
        };

        this.on_click_manage_users = (e) => {
            e.preventDefault();
            fetch('/admin/users', {
                headers: {
                 'Authorization': 'Bearer ' + this.props.jwt,
                }
            }).then(response => {
                console.log("fetched json");
                console.log({response});
                return response.json();
            }
            ).then(response => this.state.users = response);
        }

        this.on_click_manage_posts = (e) => {
            e.preventDefault();
            this.setState({show: "manage posts"});
        }

        this.on_click_manage_comments = (e) => {
            e.preventDefault();
            this.setState({show: "manage comments"});
        }

        this.on_click_manage_reports = (e) => {
            e.preventDefault();
            this.setState({show: "manage reports"});
        }

        this.on_click_show_options = (e) =>{
            e.preventDefault();
            this.setState({show: "all options"});
        }
    }

    

  render() {
      if (this.state.show === "all options"){
          return (
            <div>
              <Options on_click_manage_users={this.on_click_manage_users} />
            </div>);
      }
      if (this.state.show === "manage users"){
          return (<div>
                    <a href="#" onClick={this.on_click_show_options}>
                        <h4>
                        Show All Options
                        </h4>
                    </a>
                    <hr></hr>
                    <UserManagement users={this.state.users}/>
                </div>);
      }
      if (this.state.show === "manage posts"){
        return (<div>
            <a href="#" onClick={this.on_click_show_options}>
                <h4>
                Show All Options
                </h4>
            </a>
            <hr></hr>

        </div>);
      }
      if (this.state.show === "manage comments"){
        return (<div>
            <a href="#" onClick={this.on_click_show_options}>
                <h4>
                Show All Options
                </h4>
            </a>
            <hr></hr>

        </div>);
      }
      if (this.state.show === "manage reports"){
        return (<div>
            <a href="#" onClick={this.on_click_show_options}>
                <h4>
                Show All Options
                </h4>
            </a>
            <hr></hr>

        </div>);
      }
  }
}


export default AdminBody;
