import React, {Component} from 'react';
import CommentDeletion from '../admin/CommentDeletion'
import CommentRow from '../admin/CommentRow'

class CommentManagement extends Component {
    
    deleteComment = (commentId) => {
        fetch()
    }

    renderComments = () =>
    {
        var rows = new Array();
        for (let i = 0; i < this.props.comments.length; i++)
        {
            rows.push(<CommentRow data = {this.props.comments[i]} key={i}/>);
        }

        return rows;
    }

    render() {
        return <div>
            <CommentDeletion callback={this.props.delete_comment_callback}/>
            {this.renderComments()}
        </div>
    }
}
export default CommentManagement;
