import React, {Component} from 'react';
import './Post.css';

export class CommentSection extends Component {
    constructor(props)
    {
        super(props);
        this.state = {
            postId: this.props.postId,
            comments: []
        }
    }

    componentDidMount() {
        // Load the comments for this post
        this.retrieveFeed()
            .then(res => {
            this.setState({
                comments: res.comments
            });
    });
    }

    retrieveFeed = async () => {
        let url = '/retrieve_comments?postId=' + this.props.postId;
        const response = await fetch(url)

        const json = await response.json();

        if (response.status !== 200) {
            throw Error(json.message)
        }
        return json;
    }

    // Translates this.state.commentIds into legible React Comment objects
    renderComments = () =>
    {
        let commentsHTML = [];
        console.log(this.state.comments)
        // Add the comments
        let comments = this.state.comments;
        for (let i = 0; i < comments.length; ++i)
        {
            console.log(comments[i])
            comments.push(<Comment comment = {comments[i]}/>);
        }

        return commentsHTML;
    }

    render()
    {
        return(
            <div className="CommentSection-container">
                {this.renderComments}
            </div>
        );
    }
}

export class AddComment extends Component {
    constructor(props)
    {
        super(props);
        this.state = {
            postId: this.props.postId,
        }
    }

    render() {
        return (
            <div className="AddComment-container">

            </div>
        );
    }
}

export class Comment extends Component {
    constructor(props)
    {
        super(props);
        this.state = {
            postId: this.props.postId,
        }
    }

    render() {
        return (
            <div className='Comment-container'>
                <p>Comment</p>
                {this.props.comment.commentId}
            </div>
        );
    }
}
