import React, { Component } from 'react';
import Post from "./Post";

const NUMBER_OF_POSTS_TO_LOAD = 10;

class Feed extends Component {
    state = {
        data: null
    };

    componentDidMount() {
        // Load the feed contents
        this.retrieveFeed()
            .then(res => this.setState( {
                data: res.data
            }));
    }

    retrieveFeed = async () => {
        const response = await fetch('/user_data');
        const json = await response.json();

        if (response.status !== 200) {
            throw Error(json.message)
        }
        return json;
    }

    loadPosts = () =>
    {
        let posts = [];

        // Add the posts
        for (let i = 0; i < NUMBER_OF_POSTS_TO_LOAD; ++i)
        {
            // TODO: Get the recent posts via timestamp from express
            console.log(i);
            posts.push(<Post postName = {i}/>);
        }
    }

    render() {
        return (
            // TODO: Make a box that contains the posts
            <div className="User">
                <p>Feed</p>
                //Has some stuff like filter, then the scrollable layout with posts

                {this.loadPosts()}
            </div>
        );
    }
}


export default Feed;
