import React, { Component } from 'react';
import Post from "./Post";

const NUMBER_OF_POSTS_TO_LOAD = 10;

class Feed extends Component {
    state = {
        data: null
    };

    componentDidMount() {
        // Load the feed contents
        this.retrieveFeed()
            .then(res => this.setState( {
                data: res.data
            }));
    }

    retrieveFeed = async () => {
        const response = await fetch('/user_data');
        const json = await response.json();

        if (response.status !== 200) {
            throw Error(json.message)
        }
        return json;
    }

    loadPosts = () =>
    {
        let posts = [];

        // Add the posts
        for (let i = 0; i < 10; ++i)
        {
            // TODO: Get the recent posts via timestamp from express
            posts.push(<p>{`text`}</p>);
        }
    }

    createTable = () => {
        let table = []

        // Outer loop to create parent
        for (let i = 0; i < 3; i++) {
            let children = []
            //Inner loop to create children
            for (let j = 0; j < 5; j++) {
                children.push(<td>{`Column ${j + 1}`}</td>)
            }
            //Create the parent and add the children
            table.push(<tr>{children}</tr>)
        }
        return table
    }

    render() {
        return (
            // TODO: Make a box that contains the posts
            <div className="User">
                <p>Feed Start</p>
                //Has some stuff like filter, then the scrollable layout with posts

                {this.loadPosts()}
                {this.createTable()}
            </div>
        );
    }
}


export default Feed;
